TEMPLATE="capitulo-%02d.txt"
INDEX=1

rm -f $(printf "$TEMPLATE" $INDEX)

while read line; do
    echo "$line" >> $(printf "$TEMPLATE" $INDEX)
    if [ "$line" = "" ]; then
        # Si viene un p√°rrafo nuevo lo pasamos a otro archivo
        let INDEX++
        rm -f $(printf "$TEMPLATE" $INDEX)
    fi
done <<< "$LIPSUM"

